
public class Lesson_12 {

	/*
	 * Variable - named memory location that can change values.
	 * Integer - Whole number between -2,147,648 to 2, 147, 647
	 * 
	 * Must declare variables before you can use them.
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int variable1;
		int variable2, variable3;
		
		variable1 = 9;
		variable2 = 24;
		variable3 = 16;
		
		System.out.println("The value of variable1 is: " + variable1);
		System.out.println("Variable2 is: " + variable2 + " \nVariable3 is: " + variable3);

	}

}
