
public class Lesson_37 {

	// do-while loop guarantees at least 1 run through.
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		
		i = 1;
		
		do {
			System.out.println("The do-while loop variable is: " + i);
			i++;
		} while (i <= 15);
		
		
		/*
		while(i <= 15) {
			System.out.println("The current loop variable is: " + i);
			i++;
		}
		*/

	}

}
