
public class Lesson_40 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.round(1.75));	// rounds to nearest whole number
		
		System.out.println(Math.floor(2.75)); 	// gives lower whole number
		
		System.out.println(Math.ceil(2.05));	// gives highest whole number

	}

}
